import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Rules.css";

const apiURL = process.env.REACT_APP_API_URL || "http://3.24.50.90:3000/api";

interface Pokemon {
  name: string;
  image: string;
  reward: number;
}

const Rules: React.FC = () => {
  const [pokemonList, setPokemonList] = useState<Pokemon[]>([]);

  // ดึงข้อมูลโปเกมอนจาก API
  const fetchPokemonList = async () => {
    try {
      const response = await axios.get(`${apiURL}/all-pokemon-images`);
      setPokemonList(response.data);
    } catch (error) {
      console.error("Error fetching Pokémon list:", error);
    }
  };

  useEffect(() => {
    fetchPokemonList();
  }, []);

  return (
    <div className="rules-container">
      <h1>กติกาเกม Pokémon Slot Machine</h1>
      <div className="rules-content">
        <h2>1. วิธีการเล่น</h2>
        <p>
          - ผู้เล่นจะเลือกจำนวนเงินเดิมพันจากตัวเลือกที่กำหนด (เช่น 100, 200, 300, 500) <br />
          - กดปุ่ม <strong>"Spin"</strong> เพื่อหมุนสล็อต <br />
          - หากสัญลักษณ์เรียงตรงตามเงื่อนไขที่กำหนด ผู้เล่นจะได้รับรางวัล <br />
        </p>
        <h2>2. กติกาในการชนะ</h2>
        <p>
          - การคำนวณรางวัลจะอิงจากรูปสุดท้ายที่ปรากฏตามกติกาดังนี้:
          <ul>
            <li>ถ้าขึ้นรูปโปเกมอนตัวเดียวกันในแถวใดก็ได้ แต่ต้องขึ้นครบทุกรีล (รวม Wild) จะได้รับรางวัล 1 เท่า</li>
            <li>ถ้าขึ้นรูปโปเกมอนตัวเดียวกันในแถวเดียวกันเรียง 3 ตัว (รวม Wild) จะได้รับรางวัล 2 เท่า</li>
            <li>ถ้าขึ้นรูปโบนัสในแถวใดก็ได้ แต่ต้องขึ้นครบทุกรีล (รวม Wild) จะได้รับรางวัล 3 เท่า</li>
            <li>ถ้าขึ้นรูปโบนัสในแถวเดียวกันเรียง 3 ตัว (รวม Wild) จะได้รับรางวัล 4 เท่า</li>
          </ul>
          - รางวัลคำนวณโดยนำข้อมูลรางวัลของโปเกมอนในตารางมาคูณกับจำนวนเงินเดิมพัน <br />
        </p>
        <h2>3. เงื่อนไขโบนัส</h2>
        <p>
          - หากได้รูปสัญลักษณ์ Wild ตัวใดตัวหนึ่ง สามารถใช้แทนรูปโปเกมอนตัวอื่นได้ <br />
          - หากสัญลักษณ์โบนัสขึ้นตามเงื่อนไขที่กำหนด ผู้เล่นจะได้รับรางวัลเพิ่มตามอัตราที่ระบุ <br />
        </p>
        <h2>4. อัตราการชนะ</h2>
        <p>
          - อัตราการชนะจะขึ้นอยู่กับการตั้งค่าของระบบ <br />
          - อัตราการชนะสามารถปรับได้โดยผู้ดูแลระบบผ่านหน้า Game Settings <br />
        </p>
        <h2>5. รายละเอียดรางวัลของโปเกมอน</h2>
        <div className="pokemon-rewards">
          {pokemonList.length > 0 ? (
            pokemonList.map((pokemon) => (
              <div key={pokemon.name} className="pokemon-item">
                <img src={pokemon.image} alt={pokemon.name} className="pokemon-image" />
                <p>
                  <strong>{pokemon.name}</strong>: {pokemon.reward} คะแนน
                </p>
              </div>
            ))
          ) : (
            <p>กำลังโหลดข้อมูลโปเกมอน...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Rules;
